mapper.select:
select select j.jobId,j.jobTitle,j.releasePrice,t.typeName,j.jobSource,i.headimgurl, enrollTime,commitTime,auditTime,uj.status from user_job uj inner join job j on uj.jobId=j.jobId inner join user_info i on j.userId=i.userId inner join job_type t on j.typeId=t.typeId
uj.userId=#{userId}
and uj.status=#{status}
order by uj.status
